import "core-js/modules/es.array.push.js";
import RandomCafeButton from "@/components/RandomCafeButton.vue";
import CafeComponent from "@/components/CafeComponent.vue";
import axios from "axios";
export default {
  name: "Cafes-Component",
  components: {
    CafeComponent,
    RandomCafeButton
  },
  data() {
    return {
      cafes: []
    };
  },
  mounted: async function () {
    try {
      // alert()
      /*    alert(JSON.stringify(await fetch('http://localhost:8080/api/default/get-cafes',
              {
                // mode: 'cors',
                credentials: 'include',
                headers: {
      //                Accept: 'application/json',
      //               'Content-Type': 'application/json',
      //            'Access-Control-Allow-Origin':' *',
      // 'Access-Control-Allow-Headers': 'authorization',
      // 'Access-Control-Allow-Credentials': true
                }
              })))*/

      const cafes = JSON.parse((await axios.get('http://localhost:8080/api/default/get-cafes', {
        headers: {
          // Accept: 'application/json',
          // 'Content-Type': 'application/json'
        }
      })).data).data;
      const feedCount = (await axios.get('http://localhost:8080/api/default/get-feedbacks-count')).data;
      let feedMap = new Map();
      feedCount.forEach(v => {
        feedMap.set(v.id_cafe, v.count);
      });
      cafes.forEach(e => {
        e['show'] = true;
        e['feedbacksCount'] = feedMap.get(e.id) || 0;
      });
      this.cafes.push(...cafes);
    } catch (e) {
      console.log(JSON.stringify(e));
    }
  },
  methods: {
    async getFeedbacks(id) {
      try {
        return (await axios.get(`http://localhost:8080/api/default/get-feedbacks-by-id?id=${id}`)).data;
      } catch (e) {
        console.log(JSON.stringify(e));
      }
    },
    async saveFeedback(feedback) {
      try {
        await axios.post(`http://localhost:8080/api/default/save-feedback`, feedback, {
          headers: {
            'content-type': 'multipart/form-data'
          }
        });
      } catch (e) {
        console.log(e);
      }
    },
    randomCafe() {
      const id = Math.round(Math.random() * (this.cafes.length - 1));
      this.cafes.map((v, i) => {
        i != id ? v.show = false : v.show = true;
      });
    }
  }
};
